mapper.select:
select A.InMat_Order_No, A.InMat_Lot_No, A.InMat_No, A.InMat_Code, C.Product_Item_Name InMat_Name, C.Product_Info_STND_1 InMat_STND_1, C.Product_Info_STND_2 InMat_STND_2, sum(A.InMat_Qty) InMat_Qty, A.InMat_Unit_Price, D.Child_TBL_Type InMat_Unit, sum(A.InMat_Price) InMat_Price, A.InMat_Client_Code, B.Cus_Name InMat_Client_Name, A.InMat_Date, A.InMat_Rcv_Clsfc, E.Child_TBL_Type InMat_Rcv_Clsfc_Name from InMat_tbl A inner join Customer_tbl B on A.InMat_Client_Code = B.Cus_Code inner join Product_Info_tbl C on A.InMat_Code = C.PRODUCT_ITEM_CODE inner join DTL_TBL D on C.PRODUCT_UNIT = D.CHILD_TBL_NO inner join DTL_TBL E on A.InMat_Rcv_Clsfc = E.CHILD_TBL_NO
where A.InMat_Date >= #{startDate} and A.InMat_Date < #{endDate}
and A.InMat_Rcv_Clsfc = #{ItemSendClsfc}
and A.InMat_Code like concat('%',#{ItemCode},'%') group by A.InMat_Code, A.InMat_Client_Code with rollup
and A.InMat_Client_Code like concat('%',#{ClientCode},'%') group by A.InMat_Client_Code, A.InMat_Code with rollup

select A.InMat_Order_No, A.InMat_Lot_No, A.InMat_No, A.InMat_Code, sum(A.InMat_Qty) InMat_Qty, A.InMat_Unit_Price, sum(A.InMat_Price) InMat_Price, A.InMat_Client_Code, B.Cus_Name InMat_Client_Name, A.InMat_Date, A.InMat_Rcv_Clsfc from InMat_tbl A inner join Customer_tbl B on A.InMat_Client_Code = B.Cus_Code
where A.InMat_Date >= #{startDate} and A.InMat_Date < #{endDate}
group by A.InMat_Client_Code

select A.InMat_No, A.InMat_Date, E.CHILD_TBL_TYPE InMat_Rcv_Clsfc, A.InMat_Code, A.InMat_Client_Code, C.PRODUCT_ITEM_NAME InMat_Name, C.PRODUCT_INFO_STND_1 InMat_STND_1, C.PRODUCT_INFO_STND_2 InMat_STND_2, D.CHILD_TBL_TYPE InMat_UNIT, A.InMat_Qty, A.InMat_Unit_Price, A.InMat_Price from InMat_tbl A inner join Product_Info_tbl C on A.InMat_Code = C.PRODUCT_ITEM_CODE left outer join DTL_TBL D on C.PRODUCT_UNIT = D.CHILD_TBL_NO left outer join DTL_TBL E on A.InMat_Rcv_Clsfc = E.CHILD_TBL_NO
where A.InMat_Date >= #{startDate} and A.InMat_Date < #{endDate}
and A.InMat_Client_Code = #{clientCode}

mapper.update:
update InMat_tbl
set InMat_Check_1 = 'true'
where InMat_No = #{inMat_No} and InMat_Lot_No = #{InMat_Lot_No}
