mapper.select:
SELECT A.Order_mCus_No, A.Order_mCode, B.Cus_Name Order_mName, A.Order_mDate, A.Order_mTotal, A.Order_mDlvry_Date, A.Order_mRemarks, A.Order_mModifier, A.Order_mModify_Date, A.Order_mCheck FROM OrderMaster_tbl A inner join Customer_tbl B on A.Order_mCode = B.Cus_Code
where A.Order_mDate >= #{startDate} and Order_mDate < #{endDate}
and Order_mCode like concat('%',#{ClientCode},'%')
and not Order_mCheck = #{condition}
order by Order_mDate desc

mapper.update:
update OrderMaster_tbl
set Order_mCheck = 
	(select 
		(case
			when sum(Order_lQty) = sum(Order_lSum) then 'Y'
			when sum(Order_lSum) = 0 then 'N'
			else 'I'
		end)
		from OrderList_tbl
		where Order_lCus_No = #{inMat_Order_No}
	)
where Order_mCus_No = #{inMat_Order_No}
