mapper.update:
insert into Stock_tbl(
	S_ItemCode, S_Qty,
	S_WareHouse
) values(
	#{S_ItemCode}, #{S_Qty},
	#{S_WareHouse}
)on duplicate key update
	S_Qty = S_Qty + #{S_Qty}

mapper.insert:
insert into Stock_tbl(
	S_itemCode, S_Qty, S_WareHouse
)values(
	#{LM_ItemCode}, #{LM_Qty}, #{LM_WareHouse}
)on duplicate key update
S_Qty = S_Qty + #{LM_Qty}

mapper.update:
update Stock_tbl
set
S_Qty = S_Qty - #{LM_Qty}
where S_itemCode = #{LM_ItemCode}
and S_WareHouse = #{LT_Before}

update Stock_tbl
set
