mapper.select:
SELECT proit.Process_Inspect_LotNo, proit.Process_Inspect_EquipCode, eit.Equipment_Info_Name as Process_Inspect_EquipName, proit.Process_Inspect_ItemCode, pit.Product_Item_Name as Process_Inspect_ItemName, pit.Product_Info_STND_1 as Process_Inspect_STND_NAME_1, dt.Child_TBL_Type as Process_Inspect_Item_Clsfc_Name_1, proit.Process_Inspect_Qty, proit.Process_Inspect_Date, dt2.Child_TBL_Type as Process_Inspect_Worker FROM Process_Inspect_tbl proit INNER JOIN Equipment_Info_tbl eit ON proit.Process_Inspect_EquipCode = eit.Equipment_Info_Code INNER JOIN Product_Info_tbl pit ON proit.Process_Inspect_ItemCode = pit.Product_Item_Code INNER JOIN DTL_TBL dt ON pit.Product_Item_CLSFC_1 = dt.Child_TBL_No INNER JOIN DTL_TBL dt2 ON proit.Process_Inspect_Worker = dt2.Child_TBL_No
WHERE proit.Process_Inspect_Date >= #{startDate} AND proit.Process_Inspect_Date < #{endDate}
AND proit.Process_Inspect_ItemCode LIKE CONCAT('%',#{itemCode},'%') AND proit.Process_Inspect_EquipCode LIKE CONCAT('%',#{machineCode},'%') GROUP BY proit.Process_Inspect_LotNo
