		WorkOrder_AllottedQty = #{WorkOrder_ProductionQty},

	WorkOrder_ProductionQty = #{WorkOrder_ProductionQty}
	where WorkOrder_ONo = #{WorkOrder_ONo} and WorkOrder_EquipCode = #{WorkOrder_EquipCode}

		update WorkOrder_tbl 

