   		    WorkOrder_Worker = #{modifier}
        where workOrder_ONo = #{WorkOrder_ONo}
        and WorkOrder_WorkStatus = #{WorkStatus}


        set WorkOrder_WorkStatus = '242', WorkOrder_ReceiptTime = NOW(),
   		    WorkOrder_Worker = #{modifier}
