        and WorkOrder_WorkStatus = #{WorkStatus}

mapper.select:
SELECT A.WorkOrder_No, A.WorkOrder_ONo, A.WorkOrder_ItemCode, B.Product_Item_Name WorkOrder_ItemName, B.Product_Info_STND_1 WorkOrder_Item_STND_1, B.Product_Info_STND_2 WorkOrder_Item_STND_2, B.Product_Item_CLSFC_1, B.Product_Item_CLSFC_2, A.WorkOrder_EquipCode, C.Equipment_Info_Name WorkOrder_EquipName, A.WorkOrder_ProductionQty, A.WorkOrder_AllottedQty, A.WorkOrder_RegisterTime, A.WorkOrder_StartTime, A.WorkOrder_CompleteTime, A.WorkOrder_WorkStatus, D.Child_TBL_Type WorkOrder_WorkStatus_Name, A.WorkOrder_Worker, A.WorkOrder_Remark, A.WorkOrder_Use_Status FROM WorkOrder_tbl A inner join Product_Info_tbl B on A.WorkOrder_ItemCode = B.Product_Item_Code inner join Equipment_Info_tbl C on A.WorkOrder_EquipCode = C.Equipment_Info_Code inner join DTL_TBL D on A.WorkOrder_WorkStatus = D.Child_TBL_No where A.WorkOrder_WorkStatus in (select Child_TBL_No from DTL_TBL where New_TBL_Code ='29' and Child_TBL_Num in (0, 1, 2, 3))
and A.WorkOrder_EquipCode = #{machineCode}
and A.WorkOrder_ItemCode = #{itemCode}
order by A.WorkOrder_EquipCode, A.WorkOrder_RegisterTime
limit 2

mapper.update:
		update WorkOrder_tbl
