mapper.select:
SELECT *
FROM Crate_Lot_tbl A

where A.CL_Create_Date < (

	select
		CL_Create_Date
	from Crate_Lot_tbl B
	where B.CL_CrateCode = #{CL_CrateCode}
	and B.CL_MachineCode = #{CL_MachineCode}
)

SELECT A.*, pit.Product_Item_Name as CL_ItemName, eit.Equipment_Info_Name as CL_EquipName, pit.Product_Info_STND_1 CL_STND_1, pit.Product_Info_STND_2 CL_STND_2, dt.Child_TBL_Type CL_Item_Clsfc_Name_1, dt2.Child_TBL_Type CL_Item_Clsfc_Name_2, dt3.Child_TBL_Type CL_Item_Material, df.Defect_Qty CL_Defect_SumQty FROM (
SELECT * FROM Crate_Lot_tbl clt WHERE clt.CL_Create_Date >= #{startDate} AND clt.CL_Create_Date < #{endDate}
) A inner join Product_Info_tbl pit on A.CL_ItemCode = pit.PRODUCT_ITEM_CODE inner join Equipment_Info_tbl eit on A.CL_MachineCode = eit.EQUIPMENT_INFO_CODE INNER JOIN DTL_TBL dt ON pit.Product_Item_CLSFC_1 = dt.Child_TBL_No INNER JOIN DTL_TBL dt2 ON pit.Product_Item_CLSFC_2 = dt2.Child_TBL_No INNER JOIN DTL_TBL dt3 ON pit.Product_Material = dt3.Child_TBL_No LEFT OUTER JOIN Crate_tbl ct ON A.CL_CrateCode = ct.C_CrateCode LEFT OUTER JOIN DTL_TBL dt4 ON ct.C_Condition = dt4.Child_TBL_No left outer join ( SELECT Defect_ONo, sum(Defect_Qty) Defect_Qty FROM Defect_tbl group by Defect_ONo ) df on A.CL_LotNo = df.Defect_ONo WHERE dt4.Child_TBL_No = #{Condition}
AND A.CL_LotNo LIKE CONCAT ('%',#{LotNo},'%')
AND A.CL_ItemCode LIKE CONCAT('%',#{ItemCode},'%')

mapper.update:
update Crate_Lot_tbl
set	
CL_Qty = CL_Qty + #{CL_Qty},
CL_ProductionQty = CL_Qty
where CL_LotNo = (
	select
		AA.C_Production_LotNo
    from (
		select
			A.C_Production_LotNo
		from Crate_tbl A
		inner join Crate_Lot_tbl B on B.CL_LotNo = A.C_Production_LotNo and B.CL_MachineCode = #{CL_MachineCode}
		where C_Condition = '1'
    ) AA
);

update Crate_Lot_tbl
set
CL_ProductionQty = CL_ProductionQty + #{CL_ProductionQty}
where CL_LotNo = #{CL_LotNo}
